17: } 1: import gsap from "gsap";
 2: import { ScrollTrigger } from "gsap/ScrollTrigger";
 3: import { SplitText } from "gsap/SplitText";
 4: import Lenis from "lenis";
 5: 
 6: document.addEventListener("DOMContentLoaded", () => {
 7:   gsap.registerPlugin(ScrollTrigger, SplitText);
 8: 
 9:   const lenis = new Lenis();
10:   lenis.on("scroll", ScrollTrigger.update);
11:   gsap.ticker.add((time) => {
12:     lenis.raf(time * 1000);
13:   });
14:   gsap.ticker.lagSmoothing(0);
15: 
16:   const bannerContainer = document.querySelector(".banner-img-container");
17:   const bannerIntroTextElements = gsap.utils.toArray(".banner-intro-text");
18:   const bannerMaskLayers = gsap.utils.toArray(".mask");
19: 
20:   const bannerHeader = document.querySelector(".banner-header h1");
21:   const splitText = new SplitText(bannerHeader, { type: "words" });
22:   const words = splitText.words;
23:   gsap.set(words, { opacity: 0 });
24: 
25:   bannerMaskLayers.forEach((layer, i) => {
26:     gsap.set(layer, { scale: 0.9 - i * 0.2 });
27:   });
28:   gsap.set(bannerContainer, { scale: 0 });
29: 
30:   ScrollTrigger.create({
31:     trigger: ".banner",
32:     start: "top top",
33:     end: `+=${window.innerHeight * 4}px`,
34:     pin: true,
35:     pinSpacing: true,
36:     scrub: 1,
37:     onUpdate: (self) => {
38:       const progress = self.progress;
39: 
40:       gsap.set(bannerContainer, { scale: progress });
41: 
42:       bannerMaskLayers.forEach((layer, i) => {
43:         const initialScale = 0.9 - i * 0.2;
44:         const layerProgress = Math.min(progress / 0.9, 1.0);
45:         const currentScale =
46:           initialScale + layerProgress * (1.0 - initialScale);
47: 
48:         gsap.set(layer, { scale: currentScale });
49:       });
50: 
51:       if (progress <= 0.9) {
52:         const textProgress = progress / 0.9;
53:         const moveDistance = window.innerWidth * 0.5;
54: 
55:         gsap.set(bannerIntroTextElements[0], {
56:           x: -textProgress * moveDistance,
57:         });
58:         gsap.set(bannerIntroTextElements[1], {
59:           x: textProgress * moveDistance,
60:         });
61:       }
62: 
63:       if (progress >= 0.7 && progress <= 0.9) {
64:         const headerProgress = (progress - 0.7) / 0.2;
65:         const totalWords = words.length;
66: 
67:         words.forEach((word, i) => {
68:           const wordStartDelay = i / totalWords;
69:           const wordEndDelay = (i + 1) / totalWords;
70: 
71:           let wordOpacity = 0;
72: 
73:           if (headerProgress >= wordEndDelay) {
74:             wordOpacity = 1;
75:           } else if (headerProgress >= wordStartDelay) {
76:             const wordProgress =
77:               (headerProgress - wordStartDelay) /
78:               (wordEndDelay - wordStartDelay);
79:             wordOpacity = wordProgress;
80:           }
81: 
82:           gsap.set(word, { opacity: wordOpacity });
83:         });
84:       } else if (progress < 0.7) {
85:         gsap.set(words, { opacity: 0 });
86:       } else if (progress > 0.9) {
87:         gsap.set(words, { opacity: 1 });
88:       }
89:     },
90:   });
